package org.example.osgi.mailbox.fixed;import java.util.Properties;import org.example.osgi.mailbox.api.Mailbox;import org.osgi.framework.BundleActivator;import org.osgi.framework.BundleContext;import org.osgi.framework.Constants;public class WelcomeMailboxActivator implements BundleActivator {	public void start(BundleContext context) throws Exception {		Properties props;				Mailbox welcome = new FixedMailbox(new String[][] {			new String[] { "Welcome!", "This mailbox was installed from S3" }		});		props = new Properties();		props.put(Mailbox.NAME_PROPERTY, "s3welcome");		props.put(Constants.SERVICE_RANKING, new Integer(10));		context.registerService(Mailbox.class.getName(), welcome, props);		/*		Mailbox missions = new FixedMailbox(new String[][] {				new String[] { "Your mission...",						"Should you choose to accept it..." },				new String[] { "This message will self-destruct in 10 seconds",						"Don't say we didn't warn you" } });		props = new Properties();		props.put(Mailbox.NAME_PROPERTY, "missions");		context.registerService(Mailbox.class.getName(), missions, props);		*/	}	public void stop(BundleContext context) throws Exception {	}}